const fs = require('fs');
const path = require('path');

const iconsDir = path.join(__dirname, '..', 'assets', 'icons');

// Create directory if it doesn't exist
if (!fs.existsSync(iconsDir)) {
  fs.mkdirSync(iconsDir, { recursive: true });
}

// Police car icon
const policeCarIcon = 'data:image/svg+xml;base64,';

// Ambulance icon
const ambulanceIcon = 'data:image/svg+xml;base64,';

// Fire truck icon
const fireTruckIcon = 'data:image/svg+xml;base64,';

// Default responder icon
const defaultIcon = 'data:image/svg+xml;base64,';

// Save icons
const saveIcon = (filename, base64Data) => {
  const base64Image = base64Data.split(';base64,').pop();
  fs.writeFileSync(path.join(iconsDir, filename), base64Image, { encoding: 'base64' });
};

// Generate all icons
saveIcon('police-car.png', policeCarIcon);
saveIcon('ambulance.png', ambulanceIcon);
saveIcon('fire-truck.png', fireTruckIcon);
saveIcon('responder.png', defaultIcon);

console.log('Icons generated successfully!');
